code.installcode:
$db->hide_errors();
$vbulletin->db->query_write("CREATE TABLE IF NOT EXISTS " . TABLE_PREFIX . "nntp_groupaccess_cache ( `usergroupslist` char(100) NOT NULL, `nntpgroupslist` varchar(7000) NOT NULL, `access_level` enum('full','demo','none') NOT NULL, `template` varchar(2000) NOT NULL, `css` varchar(5000) NOT NULL, `menu` varchar(2000) NOT NULL, `demotext` varchar(5000) NOT NULL, PRIMARY KEY (`usergroupslist`) ) ENGINE=MEMORY");
$vbulletin->db->query_write("CREATE TABLE IF NOT EXISTS " . TABLE_PREFIX . "nntp_groups ( `id` int(10) unsigned NOT NULL auto_increment, `plugin_id` char(20) NOT NULL, `group_name` char(50) NOT NULL, `is_active` enum('yes','no') NOT NULL default 'no', `map_id` int(10) unsigned NOT NULL default '0', `date_create` timestamp NOT NULL default CURRENT_TIMESTAMP on update CURRENT_TIMESTAMP, PRIMARY KEY (`id`), KEY `map_id` (`map_id`), KEY `date_create` (`date_create`), KEY `plugin_active` (`plugin_id`,`is_active`) ) ");
$vbulletin->db->query_write("CREATE TABLE IF NOT EXISTS " . TABLE_PREFIX . "nntp_index ( `groupid` int(10) unsigned NOT NULL, `messageid` int(10) unsigned NOT NULL, `parentid` int(10) unsigned NOT NULL, `title` varchar(250) NOT NULL, `body` text NOT NULL, `datetime` datetime NOT NULL, `userid` int(10) unsigned NOT NULL, `username` varchar(100) NOT NULL, `deleted` enum('no','yes') NOT NULL, `messagetype` enum('forum','blog') NOT NULL, `postid` int(10) unsigned NOT NULL, PRIMARY KEY (`groupid`,`messageid`), KEY `src` (`postid`,`messagetype`,`deleted`,`groupid`,`messageid`), KEY `parent` (`parentid`,`messagetype`,`groupid`,`postid`), KEY `daemon` (`groupid`,`deleted`,`datetime`,`messageid`) ) ");
$vbulletin->db->query_write("CREATE TABLE IF NOT EXISTS " . TABLE_PREFIX . "nntp_stats ( `userid` int(10) unsigned NOT NULL, `date` date NOT NULL, PRIMARY KEY (`userid`,`date`), KEY `date` (`date`) ) ");
$vbulletin->db->query_write("CREATE TABLE IF NOT EXISTS " . TABLE_PREFIX . "nntp_userauth_cache ( `username` varchar(100) NOT NULL, `authhash` char(32) NOT NULL, `lastactivity` timestamp NOT NULL default CURRENT_TIMESTAMP on update CURRENT_TIMESTAMP, `usergroupslist` varchar(100) NOT NULL, `userid` int(10) unsigned NOT NULL, `access_granted` enum('yes','no') NOT NULL default 'no', PRIMARY KEY (`username`,`authhash`), KEY `access_granted` (`access_granted`) USING BTREE ) ENGINE=MEMORY");
$db->show_errors();

code.uninstallcode:
$db->hide_errors();

$db->query("DROP TABLE IF EXISTS " . TABLE_PREFIX . "nntp_groupaccess_cache");

$db->query("DROP TABLE IF EXISTS " . TABLE_PREFIX . "nntp_groups");

$db->query("DROP TABLE IF EXISTS " . TABLE_PREFIX . "nntp_index");

$db->query("DROP TABLE IF EXISTS " . TABLE_PREFIX . "nntp_stats");

$db->query("DROP TABLE IF EXISTS " . TABLE_PREFIX . "nntp_userauth_cache");

$db->show_errors();

